```python
import datetime
import json
from pathlib import Path

class CookieConsent:
    def __init__(self, consent_file='consent.json'):
        self.consent = False
        self.timestamp = None
        self.consent_file = Path(consent_file)
        self.load_consent()

    def get_consent(self):
        return self.consent

    def set_consent(self, consent):
        self.consent = consent
        self.timestamp = datetime.datetime.now()
        self.save_consent()

    def get_timestamp(self):
        return self.timestamp

    def load_consent(self):
        if self.consent_file.exists():
            with self.consent_file.open('r') as f:
                data = json.load(f)
                self.consent = data.get('consent', False)
                self.timestamp = datetime.datetime.fromisoformat(data.get('timestamp'))

    def save_consent(self):
        data = {
            'consent': self.consent,
            'timestamp': self.timestamp.isoformat()
        }
        with self.consent_file.open('w') as f:
            json.dump(data, f)

# Usage
