dt = T / Nt;    % Time step size
c = 2;          % Wave speed

% Initialize the solution matrix
u = zeros(Nx, Nt+1);

% Initial condition
x = linspace(0, L, Nx);
u(:, 1) = piecewise_initial_condition(x);

% Boundary conditions
u(1, :) = 0;
u(end, :) = 0;

% Finite difference method
for n = 1:Nt
    for i = 2:Nx-1
        u(i, n+1) = 2*u(i, n) - u(i, n-1) + (c*dt/dx)^2 * (u(i+1, n) - 2*u(i, n) + u(i-1, n));
    end
end

