title('Initial Condition');
xlabel('x');
ylabel('u(x,0)');

% Animate the wave motion
figure;
for n = 1:Nt+1
    plot(x, u(:, n));
    title(['Wave Motion at t = ', num2str((n-1)*dt)]);
    xlabel('x');
    ylabel('u(x,t)');
    drawnow;
end
```

